GOVUK.epaoValidate=function(n,t){var i=$(document).attr("title"),r=n.bind("invalid-form.validate",function(){if($(".js-error-summary-list").length===0)return!1;$(".js-error-summary-list").empty();$(document).attr("title","Error: "+i);r.errorList.forEach(function(n){$(".js-error-summary-list").append('<li><a href="#'+n.element.id+'">'+n.message+"<\/a><\/li>")});$(".js-error-summary").show()}).validate({onkeyup:!1,onclick:!1,onfocusout:!1,errorElement:"span",errorClass:"error-message",highlight:function(n){$(n).closest(".form-group").addClass("form-group-error")},unhighlight:function(n){$(n).closest(".form-group").removeClass("form-group-error")},rules:t.rules,messages:t.messages,submitHandler:function(n){n.submit()}})};